mapper.select:
select * from T_FTFS



            and F_ID=#{f_id}


            and RCT_ID=#{rct_id}


            and CV_ID=#{cv_id}


            1=1

select rownum,F_ID,CV_ID,RCT_ID,F_DATE,F_IS_READ,F_IS_ACCEPT,F_IS_AGREE
from (select rownum r,F_ID,CV_ID,RCT_ID,F_DATE,F_IS_READ,F_IS_ACCEPT,F_IS_AGREE
from T_FTFS  where CV_ID=#{2} and rownum <=#{1} ) t  where r>= #{0}

select rownum,F_ID,CV_ID,RCT_ID,F_DATE,F_IS_READ,F_IS_ACCEPT,F_IS_AGREE
from (select rownum r,F_ID,CV_ID,RCT_ID,F_DATE,F_IS_READ,F_IS_ACCEPT,F_IS_AGREE
from T_FTFS  where  rownum <=#{1} ) t  where r>= #{0}
